mapper.select:
SELECT * FROM t_attendance
AND employee_name LIKE CONCAT('%', #{keyword}, '%')
ORDER BY date DESC, create_time DESC LIMIT #{pageSize} OFFSET #{pageNum}

mapper.update:
UPDATE t_attendance
checkin_time = #{checkinTime}, checkout_time = #{checkoutTime}, status = #{status}, work_hours = #{workHours},
WHERE id = #{id}
